    @apply text-gray-400 border border-gray-400 hover:bg-transparent cursor-not-allowed;
}
.disabled-link-btn {
    @apply text-gray-400 cursor-not-allowed bg-transparent hover:bg-transparent;
}
.disabled-transparent-btn {
    @apply text-gray-400 cursor-not-allowed bg-transparent hover:bg-transparent;
}
.disabled-ghostwhite-btn {
    @apply text-white cursor-not-allowed bg-transparent hover:bg-transparent;
}
.disabled-ghostdark-btn {
    @apply text-marsgray-700 cursor-not-allowed bg-transparent hover:bg-transparent;
